import { useCallback, useEffect, useState } from "react";
import { motion, AnimatePresence} from 'framer-motion';
import toast, { Toaster } from  'react-hot-toast';
import { useSocket } from './hooks/useSocket';
import AuctionCard from './components/AuctionCard';
import './App.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001';

function App() {
    const [items, setItems] = useState([]);
    const [loading, setLoading] = useState(true);
    const [userId] = useState(() => {
        let id = localStorage.getItem('userId');
        if (!id) {
            id = `user_${Math.random().toString(36).substr(2, 9)}`;
            localStorage.setItem('userId', id);
    }
    return id;
});
    
    const { placeBid, on, off, isConnected, getServerTime} = useSocket(userId);

    const fetchItems = useCallback(async () => {
        try {
            const response = await fetch(`${API_URL}/api/items`);
            const data = await response.json();
            setItems(data.items || []);
            setLoading(false);
        } catch (error) {
          console.log('Error fetching items:', error);
          toast.error('Failed to load auctions');
          setLoading(false);
        }
    }, []);

    useEffect(() => {
        fetchItems();
    }, [fetchItems]); 
    
    useEffect(() => {
        const handleBidUpdate = (data) => {
            const { itemId, currentBid, highestBidder, bidCount } = data;

            setItems(prevItems => 
                prevItems.map(item => item.id === itemId ? {...item, currentBid, highestBidder, bidCount}
                    : item));

            const isMyBid = highestBidder === userId;
            if (isMyBid) {
                toast.success(`You're winning! $${currentBid.toLocaleString()}`, {
                    icon: 'ðŸŽ¯',
                    duration: 3000
                });
            } else {
                const item = items.find(i => i.id === itemId);
                if (item && item.highestBidder === userId) {
                    toast.error(`You've been outbid on ${item.title}!`, {
                        icon: 'âš ï¸',
                        duration: 4000
                    })
                }
            }    
        };

    const handleBidError = (data) => {
        const { error, message } =  data;

        if (error === 'BID_TOO_LOW') {
            toast.error(message, {
                icon: 'ðŸ’¨',
                duration: 3000
            });  
        } else if (error === 'AUCTION_ENDED') {
            toast.error('This auction has ended', {
                icon: 'â°',
                duration: 3000
            });
        } else {
            toast.error(message || 'Bid failed', {
                icon: 'âŒ',
                duration: 3000
            });
        }
    };

    const handleItemsUpdate = (data) => {
        setItems(data.items || []);
    }

    on('onBidUpdate', handleBidUpdate);
    on('onBidError', handleBidError);
    on('itemsUpdate', handleItemsUpdate);

    return () => {
        off('onBidUpdate');
        off('onBidError');
        off('itemsUpdate');
    };
}, [on, off, userId, items]);

const handleBid = useCallback((itemId, bidAmount) => {
    placeBid(itemId, bidAmount);
}, [placeBid]);

if (loading) {
    return (
        <div className="Loading-screen">
            <motion.div
              className="loading-spinner"
              animate={{ rotate: 360 }}
              transition={{ duration: 1, repeat: Infinity, ease: 'linear' }}
            >
             âš¡
            </motion.div>
            <p>Loading auctions...</p>
        </div>
    );  
}
    return (
        <div className="app">
            <Toaster position="top-right" />
     
            <AnimatePresence>
                <motion.div className='auction-grid'>
                    {items.map((item, index) => (
                    <motion.div
                        key={item.id}
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ delay: index * 0.05 }}
                    >
                        <AuctionCard
                        item={item}
                        userId={userId}
                        onBid={handleBid}
                        getServerTime={getServerTime}
                        />
                        </motion.div>
                    ))}
                </motion.div>
            </AnimatePresence>
        </div>
    );
}
